const int MAGIC = 1000000;

int d[5];
int pula[] = {0, 1, 2, 3, 6, 8, 12};

int main() {
    freopen("blas.in", "r", stdin);
    freopen("blas.out", "w", stdout);

    int t;
    scanf("%d", &t);
    while (t--) {
        int n;
        scanf("%d", &n);
        if (n < 7) {
            printf("%d\n", pula[n]);
            continue;
        }
        n -= 6;
        int go = 5;
        if (n < MAGIC) {
            d[1] = 18;
            d[2] = 27;
            d[3] = 39;
            d[4] = 58;
        } else {
            int keep, laba = 0;
            for (int i = MAGIC; i <= n; i += MAGIC)
                if (i <= n) {
                    keep = i;
                    laba += 4;
                }
            int i = laba - 4;
            d[(keep - 3) % 5] = mlc[i];
            d[(keep - 2) % 5] = mlc[i + 1];
            d[(keep - 1) % 5] = mlc[i + 2];
            d[keep % 5] = mlc[i + 3];
            go = keep + 1;
        }
        for (int i = go; i <= n; ++i) {
            if (i % 2 == 0)
                d[i % 5] = d[(i - 1) % 5] + d[(i - 3) % 5] + 1;
            else
                d[i % 5] = d[(i - 1) % 5] + d[(i - 3) % 5];
            if (d[i % 5] >= mod)
                d[i % 5] -= mod;
            }
        printf("%d\n", d[n % 5]);
    }

    return 0;
}
